import { makeStyles, Typography } from "@material-ui/core";
import React from "react";

// >> CSS
const useStyles = makeStyles(() => ({
  container: {
    width: "100%",
    height: "100%",
  },
}));

const GameStatePopup = ({ isCorrect, turn, solution }) => {
  // >> Styles
  const classes = useStyles();

  // >> Return
  return (
    <div className={classes.container}>
      {isCorrect && (
        <div>
          <Typography>You Win!</Typography>
          <Typography>You found the word in {turn} guesses</Typography>
        </div>
      )}
      {!isCorrect && (
        <div>
          <Typography>Better Luck Next Time</Typography>
          <Typography>The word was {solution} </Typography>
        </div>
      )}
    </div>
  );
};

export default GameStatePopup;
