import React from 'react';
import { makeStyles, Typography } from '@material-ui/core';
import PropTypes from 'prop-types';
import FormControlTooltip from './form-controls/FormControlTooltip';

// >> CSS
const useStyles = makeStyles((theme) => ({
  headingContainer: {
    display: 'flex',
    alignItems: 'center',
    // justifyContent: 'center',
  },
  headingText: {
    width: 'fit-content',
    borderBottom: `3px solid ${theme.palette.primary.main}`,
  },
}));
// >> CSS

/**
 * @param {{ text: string,
 * variant: variantTypeDef,
 * tooltip: string,
 * tooltipVariant: tooltipVariantTypeDef,
 * tooltipColor: tooltipColorTypeDef,
 * tooltipIconStyles: object
 * }} props
 *
 * + `props.text` - `*Required` the text to display in the heading
 * + `props.variant` - the [`MUI Typography`](https://v4.mui.com/components/typography/#component) variant
 * + `props.tooltip` -  the text to display in the tooltip
 * + `props.tooltipVariant` -  the icon to display
 * + `props.tooltipColor` -  the color of the icon
 * + `props.tooltipIconStyles` -  pass in an object of styles if you wish to override this components styling for the icon
 *
 * @typedef {'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6' | 'body1' | 'body2' | 'subtitle1' | 'subtitle2' | 'button' | 'caption' | 'overline' | 'inherit'} variantTypeDef
 * @typedef {'info' | 'warning' | 'error' | 'help' | 'success'} tooltipVariantTypeDef
 * @typedef {'default' | 'success' | 'warning' | 'error' | 'primary' | 'secondary'} tooltipColorTypeDef
 *
 * @returns a heading component
 *
 * @example
 * <SectionHeading text='Products' />
 */
const SectionHeading = ({
  text,
  variant = 'h5',
  tooltip,
  tooltipVariant = 'help',
  tooltipColor,
  tooltipIconStyles,
}) => {
  // >> Styles
  const classes = useStyles();

  // > RENDER
  return (
    <div className={classes.headingContainer}>
      {/* Tooltip */}
      {tooltip && (
        <FormControlTooltip
          tooltip={tooltip}
          tooltipVariant={tooltipVariant}
          tooltipColor={tooltipColor}
          tooltipIconStyles={tooltipIconStyles}
        />
      )}
      {/* Heading */}
      <Typography variant={variant} className={classes.headingText}>
        {text}
      </Typography>
    </div>
  );
};

// >> PropTypes
SectionHeading.propTypes = {
  text: PropTypes.string,
  variant: PropTypes.oneOf([
    'h1',
    'h2',
    'h3',
    'h4',
    'h5',
    'h6',
    'body1',
    'body2',
    'subtitle1',
    'subtitle2',
    'button',
    'caption',
    'overline',
    'inherit',
  ]),
  tooltip: PropTypes.string,
  tooltipVariant: PropTypes.oneOf([
    'info',
    'warning',
    'error',
    'help',
    'success',
  ]),
  tooltipColor: PropTypes.oneOf([
    'default',
    'success',
    'warning',
    'error',
    'primary',
    'secondary',
  ]),
  tooltipIconStyles: PropTypes.object,
};

export default React.memo(SectionHeading);
